import { useState, useEffect } from "react";
import { ethers } from "ethers";
import { motion } from "framer-motion";
import Confetti from "react-confetti";
import { useWindowSize } from "react-use";
import { Web3Storage } from "web3.storage";
import "./index.css";

// 你的合约地址
const CONTRACT_ADDRESS = "0x95b7ec3774dfef85ab4995a518effae8ffef7d81";
const ABI = [
  {
    inputs: [
      { internalType: "address", name: "to", type: "address" },
      { internalType: "string", name: "ipfsCID", type: "string" }
    ],
    name: "mintWithEncryptedMetadata",
    outputs: [{ internalType: "uint256", name: "", type: "uint256" }],
    stateMutability: "nonpayable",
    type: "function"
  }
];

const NFT_NAME = "ZamaPuff";
const NFT_DESC = "这是 ZAMA 吉祥物的 NFT，象征智慧与好奇心，独一无二的数字收藏品。";
const catImage = "https://i.imgur.com/YUeySct.png";

const SEPOLIA_PARAMS = {
  chainId: "0xaa36a7",
  chainName: "Sepolia Test Network",
  nativeCurrency: { name: "Sepolia Ether", symbol: "ETH", decimals: 18 },
  rpcUrls: ["https://rpc.sepolia.org"],
  blockExplorerUrls: ["https://sepolia.etherscan.io"]
};

// 替换成你自己的 Web3Storage token
const WEB3STORAGE_TOKEN = "YOUR_WEB3STORAGE_API_KEY";

let fheInstance = null;

function App() {
  const [walletAddress, setWalletAddress] = useState(null);
  const [showFireworks, setShowFireworks] = useState(false);
  const [txHash, setTxHash] = useState(null);
  const [fheReady, setFheReady] = useState(false);
  const [minting, setMinting] = useState(false);
  const [progress, setProgress] = useState(0);
  const { width, height } = useWindowSize();

  // 初始化 FHE SDK
  useEffect(() => {
    const loadFHE = async () => {
      try {
        const { initSDK, createInstance } = await import(
          "https://cdn.zama.ai/relayer-sdk-js/0.2.0/relayer-sdk-js.js"
        );

        await initSDK();

        fheInstance = await createInstance({
          aclContractAddress: '0x687820221192C5B662b25367F70076A37bc79b6c',
          kmsContractAddress: '0x1364cBBf2cDF5032C47d8226a6f6FBD2AFCDacAC',
          inputVerifierContractAddress: '0xbc91f3daD1A5F19F8390c400196e58073B6a0BC4',
          verifyingContractAddressDecryption: '0xb6E160B1ff80D67Bfe90A85eE06Ce0A2613607D1',
          verifyingContractAddressInputVerification: '0x7048C39f048125eDa9d678AEbaDfB22F7900a29F',
          chainId: 11155111,
          gatewayChainId: 55815,
          network: window.ethereum,
          relayerUrl: 'https://relayer.testnet.zama.cloud',
        });

        setFheReady(true);
        console.log("Zama SDK 初始化完成");
      } catch (err) {
        console.error("加载 Zama SDK 失败:", err);
      }
    };
    loadFHE();
  }, []);

  const shortenAddress = (address) =>
    address ? address.slice(0,6) + "..." + address.slice(-4) : "";

  const getContract = (signer) => new ethers.Contract(CONTRACT_ADDRESS, ABI, signer);

  const connectWallet = async () => {
    if (!window.ethereum) { alert("请安装 MetaMask!"); return; }
    try {
      const chainId = await window.ethereum.request({ method: "eth_chainId" });
      if (chainId !== SEPOLIA_PARAMS.chainId) {
        try {
          await window.ethereum.request({
            method: "wallet_switchEthereumChain",
            params: [{ chainId: SEPOLIA_PARAMS.chainId }]
          });
        } catch (switchError) {
          if(switchError.code === 4902){
            await window.ethereum.request({
              method: "wallet_addEthereumChain",
              params: [SEPOLIA_PARAMS]
            });
          } else { throw switchError; }
        }
      }
      const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
      setWalletAddress(accounts[0]);
    } catch(err) {
      console.error(err);
      alert("连接钱包失败: " + err.message);
    }
  };

  const disconnectWallet = () => {
    setWalletAddress(null);
    setShowFireworks(false);
    setTxHash(null);
  };

  const handleMint = async () => {
    if (!walletAddress) { alert("请先连接钱包"); return; }
    if (!fheReady) { alert("SDK 初始化中，请稍等"); return; }

    setMinting(true);
    setProgress(0);

    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = getContract(signer);

      // FHE 加密 NFT 元数据
      const encodedText = ethers.toUtf8Bytes(`${NFT_NAME} - ${NFT_DESC}`);
      const buffer = fheInstance.createEncryptedInput(CONTRACT_ADDRESS, await signer.getAddress());
      for (let i = 0; i < encodedText.length; i++) {
        buffer.add64(BigInt(encodedText[i]));
        setProgress(Math.floor(((i + 1) / encodedText.length) * 50)); // 加密进度 0~50%
      }
      const ciphertext = await buffer.encrypt();

      // 上传 FHE 加密数据到 IPFS
      const web3storage = new Web3Storage({ token: WEB3STORAGE_TOKEN });
      const blob = new Blob([JSON.stringify(ciphertext)], { type: "application/json" });
      const file = new File([blob], `${NFT_NAME}_fhe.json`);
      const cid = await web3storage.put([file], {
        name: `${NFT_NAME}-encrypted`,
        maxRetries: 3,
        onStoredChunk: (size, total) => {
          setProgress(50 + Math.floor((size / total) * 50)); // 上传进度 50~100%
        }
      });

      console.log("IPFS CID:", cid);

      // 链上 Mint
      const tx = await contract.mintWithEncryptedMetadata(
        await signer.getAddress(),
        cid,
        { gasLimit: 500_000 }
      );

      setTxHash(tx.hash);
      setShowFireworks(true);
    } catch(err) {
      console.error(err);
      alert("Mint 失败: " + err.message);
    } finally {
      setMinting(false);
      setProgress(0);
    }
  };

  return (
    <div style={{ textAlign:"center", padding:"2rem", position:"relative" }}>
      {showFireworks && <Confetti width={width} height={height} />}
      <div style={{ position:"absolute", top:"1rem", right:"1rem" }}>
        <button
          style={{ padding:"0.3rem 0.8rem", borderRadius:"0.5rem", cursor:"pointer" }}
          onClick={walletAddress ? disconnectWallet : connectWallet}>
          {walletAddress ? shortenAddress(walletAddress) : "连接钱包"}
        </button>
      </div>

      <h1>ZAMA NFT DApp</h1>
      <h2>{NFT_NAME}</h2>

      <motion.img
        src={catImage}
        alt="cat"
        style={{ width:300, height:300, borderRadius:"1rem" }}
        initial={{ opacity:0, scale:0.8 }}
        animate={{ opacity:1, scale:1 }}
        transition={{ duration:0.5 }}
      />

      <p style={{ maxWidth:"400px", margin:"0.5rem auto" }}>{NFT_DESC}</p>

      {!showFireworks && (
        <div style={{ marginTop:"1rem" }}>
          <button
            style={{ padding:"0.5rem 1.5rem", fontSize:"1rem", borderRadius:"0.5rem", cursor:"pointer" }}
            onClick={handleMint}
            disabled={!walletAddress || !fheReady || minting}>
            {minting ? `Minting... ${progress}%` : "Mint ZAMA-NFT"}
          </button>
        </div>
      )}

      {showFireworks && (
        <div style={{ marginTop:"1rem" }}>
          <h2>Mint 成功！</h2>
          {txHash && (
            <p>
              交易: <a href={`https://sepolia.etherscan.io/tx/${txHash}`} target="_blank" rel="noopener noreferrer">{txHash}</a>
            </p>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
