import { useState, useEffect } from "react";
import { ethers } from "ethers";
import { motion } from "framer-motion";
import Confetti from "react-confetti";
import { useWindowSize } from "react-use";
import { NFTStorage, File } from 'nft.storage';
import "./index.css";

const CONTRACT_ADDRESS = "0x4c020a37c1eae6138c5bbe028fe17992b3902f9f";
const ABI = [
  {
    inputs: [
      { internalType: "address", name: "to", type: "address" },
      { internalType: "string", name: "cid", type: "string" }
    ],
    name: "mintWithCID",
    outputs: [{ internalType: "uint256", name: "", type: "uint256" }],
    stateMutability: "nonpayable",
    type: "function"
  }
];

const NFT_NAME = "ZamaPuff";
const NFT_DESC = "这是 ZAMA 吉祥物的 NFT，象征智慧与好奇心，独一无二的数字收藏品。";
const catImage = "https://i.imgur.com/YUeySct.png";
const NFT_STORAGE_API_KEY = "YOUR_NFT_STORAGE_API_KEY"; // NFT.Storage API Key

let fheInstance = null;

function App() {
  const [walletAddress, setWalletAddress] = useState(null);
  const [showFireworks, setShowFireworks] = useState(false);
  const [txHash, setTxHash] = useState(null);
  const [fheReady, setFheReady] = useState(false);
  const [minting, setMinting] = useState(false);
  const [progress, setProgress] = useState(0);
  const { width, height } = useWindowSize();

  // 初始化 Zama SDK
  useEffect(() => {
    const loadFHE = async () => {
      try {
        const { initSDK, createInstance } = await import(
          "https://cdn.zama.ai/relayer-sdk-js/0.2.0/relayer-sdk-js.js"
        );
        await initSDK();
        fheInstance = await createInstance({
          aclContractAddress: '0x687820221192C5B662b25367F70076A37bc79b6c',
          kmsContractAddress: '0x1364cBBf2cDF5032C47d8226a6f6FBD2AFCDacAC',
          inputVerifierContractAddress: '0xbc91f3daD1A5F19F8390c400196e58073B6a0BC4',
          verifyingContractAddressDecryption: '0xb6E160B1ff80D67Bfe90A85eE06Ce0A2613607D1',
          verifyingContractAddressInputVerification: '0x7048C39f048125eDa9d678AEbaDfB22F7900a29F',
          chainId: 11155111,
          gatewayChainId: 55815,
          network: window.ethereum,
          relayerUrl: 'https://relayer.testnet.zama.cloud',
        });
        setFheReady(true);
        console.log("Zama SDK 初始化完成");
      } catch (err) {
        console.error("加载 Zama SDK 失败:", err);
      }
    };
    loadFHE();
  }, []);

  const connectWallet = async () => {
    if (!window.ethereum) { alert("请安装 MetaMask!"); return; }
    try {
      const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
      setWalletAddress(accounts[0]);
    } catch(err) {
      alert("连接钱包失败: " + err.message);
    }
  };

  const getContract = (signer) => new ethers.Contract(CONTRACT_ADDRESS, ABI, signer);

  const handleMint = async () => {
    if (!walletAddress || !fheReady) return;

    setMinting(true);
    setProgress(0);

    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = getContract(signer);

      // 1️⃣ FHE 分块加密 metadata
      const encodedText = ethers.toUtf8Bytes(`${NFT_NAME} - ${NFT_DESC}`);
      const chunkSize = 32;
      let ciphertextHandles = [];

      for (let i = 0; i < encodedText.length; i += chunkSize) {
        const chunk = encodedText.slice(i, i + chunkSize);
        const buffer = fheInstance.createEncryptedInput(CONTRACT_ADDRESS, await signer.getAddress());
        for (let j = 0; j < chunk.length; j++) buffer.add64(BigInt(chunk[j]));
        const ciphertext = await buffer.encrypt();
        ciphertextHandles.push(...ciphertext.handles);

        setProgress(Math.floor(((i + chunk.length) / encodedText.length) * 100));
        await new Promise(res => setTimeout(res, 50));
      }

      // 2️⃣ 上传到 NFT.Storage
      const client = new NFTStorage({ token: NFT_STORAGE_API_KEY });
      const blob = new Blob([JSON.stringify(ciphertextHandles)]);
      const cid = await client.storeBlob(blob);
      console.log("NFT CID:", cid);

      // 3️⃣ 链上 mint
      const tx = await contract.mintWithCID(await signer.getAddress(), cid, { gasLimit: 800_000 });
      setTxHash(tx.hash);
      setShowFireworks(true);

    } catch(err) {
      console.error(err);
      alert("Mint 失败: " + err.message);
    } finally {
      setMinting(false);
      setProgress(0);
    }
  };

  return (
    <div style={{ textAlign:"center", padding:"2rem", position:"relative" }}>
      {showFireworks && <Confetti width={width} height={height} />}
      <button onClick={walletAddress ? () => setWalletAddress(null) : connectWallet}>
        {walletAddress ? walletAddress.slice(0,6)+"..."+walletAddress.slice(-4) : "连接钱包"}
      </button>

      <h1>{NFT_NAME}</h1>
      <motion.img src={catImage} alt="cat" style={{ width:300, height:300 }} />
      <p>{NFT_DESC}</p>

      <button onClick={handleMint} disabled={!walletAddress || !fheReady || minting}>
        {minting ? `Minting... ${progress}%` : "Mint NFT"}
      </button>

      {showFireworks && txHash && (
        <p>交易: <a href={`https://sepolia.etherscan.io/tx/${txHash}`} target="_blank">{txHash}</a></p>
      )}
    </div>
  );
}

export default App;
